package control_statement;

public class BreakWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		while(a<=10) {
			if(a==5)
				break;
			System.out.println("Ststement :"+a);
			a++;
		}
		System.out.println("end of program");

	}

}
